// Generated by CoffeeScript 1.9.0
(function() {
  var current, score, socket, total, uuid, wins;

  socket = io.connect();

  current = '';

  uuid = '';

  wins = 0;

  total = 0;

  score = 'まだ始まってないよ';

  $("#myCount").text(0);

  $("#enemyCount").text(0);

  socket.on('login', function(data) {
    uuid = data['uuid'];
    current = data['current'];
    $("#keyword").text(current);
    $("#textbox").val('');
    return $("#score").text(score);
  });

  socket.on('updateMembers', function(members) {
    $("#members").text(members);
    if (members > 1) {
      $("#textbox").val('');
      return $("#textbox").removeAttr('disabled');
    } else {
      $("#textbox").val('');
      return $("#textbox").attr('disabled', 'disabled');
    }
  });

  socket.on('notify', function(data) {
    total += 1;
    if (data['uuid'] === uuid) {
      $("#myWords").prepend('<li>' + data['before'] + '</li>');
      wins += 1;
      $("#myCount").text(wins);
    } else {
      $("#enemyWords").prepend('<li class="lose">' + data['before'] + '</li>');
      $("#enemyCount").text(total - wins);
    }
    current = data['current'];
    $("#keyword").text(current);
    $("#textbox").val('');
    score = Math.floor(wins / total * 100);
    $("#score").text(score + '%(' + wins + '/' + total + ')');
    if (wins >= 10 || total - wins >= 10) {
      $("#textbox").val('');
      $("#textbox").attr('disabled', 'disabled');
      if (wins >= 10) {
        alert('You Win');
      } else {
        alert('You lose');
      }
    }
  });

  $(window).keydown(function(e) {
    if (e.which === 13) {
      if (current === $("#textbox").val()) {
        socket.emit('send', {
          uuid: uuid,
          answer: $("#textbox").val()
        });
      } else {
        $('body').css('background-color', 'red');
        $(this).delay(500).queue(function() {
          $('body').css('background-color', 'white');
          return $(this).dequeue();
        });
      }
    }
  });

}).call(this);
